import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Title } from '@angular/platform-browser';

import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  pageTitle = 'Login | Maungano Food Express';
  loginForm!: FormGroup;
  fieldTextType!: boolean;
  submitted: boolean = false;
  successMsg!: string;
  errorMsg: any;
  loading = false;
  public loadingMsg = 'Authenticating...Please wait';
  _userData: any;
  _state!: number;
  _visible!: boolean;

  constructor(
    private router: Router,
    private fb: FormBuilder,
    //public dialogRef: MatDialogRef<LoginComponent>, @Inject(MAT_DIALOG_DATA) public data: DialogData
  ) {

  }

  ngOnInit(): void {
    // this.title.setTitle(this.pageTitle);

    this.loginForm = this.fb.group({
      email: [
        null,
        [
          Validators.required,
          Validators.pattern('^[a-z0-9._%+-]+@[a-z0-9.-]+.[a-z]{2,4}$'),
        ],
      ],
      password: [null, Validators.required],
    });
  }

    //get form controls
    get f() {
      return this.loginForm.controls;
    }

    //Toggle show password
    toggleFieldTextType() {
      this.fieldTextType = !this.fieldTextType;
    }

      //Share user data via shared service
  sendUserData(message: string) {
    console.log(message);

  }

    //child page toggler
    TogglePage(pageValue: string){

      switch(pageValue) {
        case 'register':
          this._visible = true 
          break;
        case 'login':
          this._visible = false
          break;
      }
    }

    login() {
      this.errorMsg = '';
      this.submitted = true;

      // this._state = +localStorage.getItem('stateToken')
      console.log("state value", this._state)

      if (this._state){
        if (this.loginForm.invalid) {
          return;
        }
        this.loading = true;
    }
  }

}
